INSERT INTO `page_translate` (`id`, `foreign_id`, `lang`, `key`, `value`) VALUES
(31, 1, 'pl', 'title', 'Home'),
(32, 1, 'pl', 'text', '<h1></h1>'),
(33, 1, 'pl', 'meta_title', 'Lorem ipsum'),
(34, 2, 'pl', 'title', 'Kontakt'),
(35, 2, 'pl', 'text', '<div class="mail-box">\r\n<table>\r\n<thead>\r\n<tr>\r\n<td>DZIAŁ</td>\r\n<td>PRZEZNACZENIE</td>\r\n<td>ADRES E-MAIL</td>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td>Redakcja</td>\r\n<td>Uwagi i komentarze dotyczące treści serwisu</td>\r\n<td><a href="mailto:redakcja@jakprawnie.pl">redakcja@jakprawnie.pl</a></td>\r\n</tr>\r\n<tr>\r\n<td>Marketing</td>\r\n<td>Promocja, reklama, kontakt z mediami</td>\r\n<td><a href="mailto:marketing@jakprawnie.pl">marketing@jakprawnie.pl</a></td>\r\n</tr>\r\n<tr>\r\n<td>Praca</td>\r\n<td>Praca w serwisie</td>\r\n<td><a href="mailto:praca@jakprawnie.pl">praca@jakprawnie.pl</a></td>\r\n</tr>\r\n<tr>\r\n<td>Biuro</td>\r\n<td>Sprawy organizacyjne, propozycje wsp&oacute;łpracy</td>\r\n<td><a href="mailto:biuro@jakprawnie.pl">biuro@jakprawnie.pl</a></td>\r\n</tr>\r\n<tr>\r\n<td>Biuro obsługi klienta</td>\r\n<td>korzystanie z serwisu, logowanie, płatności, faktury, logowanie</td>\r\n<td><a href="mailto:biuro@jakprawnie.pl">biuro@jakprawnie.pl</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>'),
(36, 2, 'pl', 'meta_title', 'Kontakt'),
(37, 3, 'pl', 'title', 'Porada prawna'),
(38, 3, 'pl', 'text', '<p>Porada prawna</p>'),
(39, 3, 'pl', 'meta_title', 'Porada prawna'),
(40, 4, 'pl', 'title', 'Oferta dla firm'),
