INSERT INTO `widget` (`id`, `title`, `html`) VALUES
('ads-box-1', 'Ads box 1', '  <div id="ads-box-1"><h3>Miejsce na reklamę</h3></div>'),
('contact-info-box', 'Dane kontaktowe ze strony kontakt', '                <div id="contact-info-box" class="contact-data">\r\n\r\n                    <h3>Serwis <strong>jakprawnie.pl</strong> jest stworzony i prowadzony przez:</h3>\r\n                    <h3><span> </span>Grupa Konsensus sp. z o.o.</h3>\r\n                    <h3><span><i class="fa fa-map-marker"></i></span>ul. Słowackiego 4/4, 32-500 <strong>Chrzanów</strong></h3>\r\n                    <h3><span><i class="fa fa-phone"></i></span>T: + 48 502 635 446</h3>\r\n                    <h3><span> </span>FAX: + 48 502 635 446</h3>\r\n                    <h3><span> </span>e-mail: <a href="mailto:kancelaria@jakprawnie.pl">kancelaria@jakprawnie.pl</a></h3>\r\n\r\n                </div> '),
('footer-info-box', 'Lewy box w stopce', '           <p class="portal-info">\r\n               Portal <a href="http://jakprawnie.pl">jakprawnie.pl</a> jest własnością spółki\r\nGrupa Konsensus sp. z o.o. zarejestrowanej w Sądzie Rejonowym dla Krakowa-Śródmieścia w Krakowie\r\nXII Wydział Gospodarczy KRS pod numerem 000050634<br>\r\nKapitał zakładowy: 20.000,00 zł<br>\r\nnr NIP: 628-226-07-02<br>\r\n            </p>\r\n\r\n            <div class="social">\r\n\r\n                <a href="https://www.facebook.com/jakprawnie"><i class="fa fa-facebook"></i></a>\r\n                <a href="https://twitter.com/jakprawnie"><i class="fa fa-twitter"></i></a>\r\n               <!-- <a href="#"><i class="fa fa-google-plus"></i></a>-->\r\n                <a href="http://jakprawnie.pl/rss/news.rss"><i class="fa fa-rss"></i></a>\r\n\r\n            </div>'),
('newsletter-box', 'Zapisz się do newslettera', '        <div id="newsletter-box">\r\n            <!-- Swiper -->\r\n            <div class="swiper-container">\r\n                <div class="swiper-wrapper">\r\n                    <div class="swiper-slide"><img class="img-responsive" src="/assets/img/newsletter1.png" alt=""></div>\r\n                    <div class="swiper-slide"><img class="img-responsive" src="/assets/img/newsletter1.png" alt=""></div>\r\n                    <div class="swiper-slide"><img class="img-responsive" src="/assets/img/newsletter1.png" alt=""></div>\r\n                </div>\r\n                <!-- Add Pagination -->\r\n                <div class="swiper-pagination"></div>\r\n            </div>\r\n\r\n\r\n            <div class="newsletter-form">\r\n                <form action="/newsletter.html" method="post" id="formnewsletter">\r\n                      <div class="form-group">\r\n                    <input id="formnewsletter-email" name="email" type="email" required placeholder="Zapisz się do newslettera" value="">\r\n                    <a href=""  id="submitnewsletter"><i style="cursor: pointer"class="fa fa-envelope-o"></i></a>\r\n                    </div>\r\n                    \r\n\r\n                <div class="form-group">\r\n\r\n                    <input type="checkbox" name="newsletter-zgoda" required>\r\n                    <label>Wyrażam zgodę na otrzymywanie informacji drogą elektroniczną</label>\r\n\r\n                </div>\r\n                  \r\n                    <input id="wyslij-newsletter" type="submit" style="display: none">\r\n                </form>\r\n            </div>\r\n\r\n        </div>'),
('offer-box', 'Offer box - sidebar', '        <div class="offer-box">\r\n\r\n            <a class="info" href="/wycena.html">\r\n\r\n                <div>\r\n\r\n                    Chcesz dowiedzieć się więcej?<br>\r\n                    Napisz!\r\n\r\n                    <img src="/assets/img/offer-box-info.png" alt="">\r\n\r\n                </div>\r\n\r\n            </a>\r\n\r\n            <a class="pismo" href="/wycena.html">\r\n\r\n                <div>\r\n\r\n                    Potrzebujesz pisma?<br>\r\n                    napiszemy je dla ciebie!\r\n\r\n                    <img src="/assets/img/offer-box-pismo.png" alt="">\r\n\r\n                </div>\r\n\r\n            </a>\r\n\r\n            <a class="pelnomocnik" href="/wycena.html">\r\n\r\n                <div>\r\n\r\n                    Szukasz pełnomocnika?<br>\r\n                    służymy pomocą!\r\n\r\n                    <img src="/assets/img/offer-box-pelnomocnik.png" alt="">\r\n\r\n                </div>\r\n\r\n            </a>\r\n\r\n        </div>'),
('our-team-box', 'Nasz zespół - strona główna', '    <div id="our-team-box">\r\n        <h2 class="col-lg-12 noleft"><span class="bold">Nasz</span> zespół</h2>\r\n        <div class="col-lg-12 entry-items">\r\n            <div class="swiper-container">\r\n                <div class="swiper-wrapper">\r\n                    <div class="swiper-slide">                       \r\n                        <div class="entry-item">\r\n                            <div class="entry-person person-1">\r\n\r\n                            </div>\r\n                            <div class="entry-caption">\r\n                                <h3>Jan Kowalski</h3>\r\n                                <div class="entry-info">Adwokat<br>specjalność: prawo karne</div>\r\n                                <div class="entry-subinfo">\r\n                                    <p>Morbi a mi quis felis luctus rhoncus. Fusce\r\n                                        euismod dignissim diam, ut faucibus\r\n                                        <a class="read-more" href="#">czyt. więcej»</a></p>\r\n                                    <a class="zadaj-pytanie" href="#">Zadaj pytanie</a><p></p>\r\n                                </div>\r\n                            </div>\r\n\r\n                        </div>\r\n                    </div>\r\n                    <div class="swiper-slide">\r\n                        <div class="entry-item">\r\n                            <div class="entry-person person-2">\r\n\r\n                            </div>\r\n                            <div class="entry-caption">\r\n                                <h3>Agata Nowak</h3>\r\n                                <div class="entry-info">Adwokat<br>specjalność: prawo autorskie</div>\r\n                                <div class="entry-subinfo">\r\n                                    <p>Morbi a mi quis felis luctus rhoncus. Fusce\r\n                                        euismod dignissim diam, ut faucibus\r\n                 